import { useEffect, useState } from "react";

function App() {
  const [goals, setGoals] = useState([]);
  const [newGoal, setNewGoal] = useState("");

  const fetchGoals = () => {
    fetch("http://127.0.0.1:5000/api/goals")
      .then((res) => res.json())
      .then((data) => setGoals(data))
      .catch((err) => console.error(err));
  };

  useEffect(() => {
    fetchGoals();
  }, []);

  const addGoal = () => {
    if (!newGoal) return alert("Title is required");

    fetch("http://127.0.0.1:5000/api/goals", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ title: newGoal }),
    })
      .then(() => {
        setNewGoal("");
        fetchGoals();
      })
      .catch((err) => console.error(err));
  };

  const toggleCompleted = (id, current) => {
    fetch(`http://127.0.0.1:5000/api/goals/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ completed: !current }),
    })
      .then(() => fetchGoals())
      .catch((err) => console.error(err));
  };

  const deleteGoal = (id) => {
    fetch(`http://127.0.0.1:5000/api/goals/${id}`, {
      method: "DELETE",
    })
      .then(() => fetchGoals())
      .catch((err) => console.error(err));
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>SkillTrack Goals</h1>

      <input
        type="text"
        placeholder="Enter new goal"
        value={newGoal}
        onChange={(e) => setNewGoal(e.target.value)}
      />
      <button onClick={addGoal} style={{ marginLeft: "10px" }}>
        Add Goal
      </button>

      {goals.length === 0 && <p>No goals yet</p>}

      <ul>
        {goals.map((goal) => (
          <li key={goal.id} style={{ marginTop: "10px" }}>
            <span
              style={{
                textDecoration: goal.completed ? "line-through" : "none",
                cursor: "pointer",
              }}
              onClick={() => toggleCompleted(goal.id, goal.completed)}
            >
              {goal.title} {goal.completed ? "✅" : ""}
            </span>
            <button
              onClick={() => deleteGoal(goal.id)}
              style={{ marginLeft: "10px" }}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
